import React from "react";

const  Darkmode = () => {
    const setDarkMode = () => {
        document.querySelector("body").setAttribute("dark-theme", "dark");
    };

    const setLightMode = () => {
        document.querySelector("body").setAttribute("dark-theme", "light");
    };

    const toggleTheme = (e) => {
        if (e.target.checked) setDarkMode();
       else setLightMode()
    };

    return(
        <div className='dark_mode'>
            <input
            className='dark_mode_input'
            type='checkbox'
            id='darkmode-toggle'
            onChange={toggleTheme}
            />
            <label className='dark_mode_label' for='darkmode-toggle'>
                 <img className='sun' src= 'images/akar-icons_sun.png' alt=""/>
                 <img className='sun' src= 'images/brightness.png' alt=""/>
            </label>
        </div>

    )
}
export default  Darkmode;
